package com.example.cialo.services.apiimport com.example.cialo.BuildConfigimport com.example.cialo.utils.Operationimport com.google.gson.Gsonimport com.jakewharton.retrofit2.adapter.rxjava2.RxJava2CallAdapterFactoryimport okhttp3.MediaTypeimport okhttp3.RequestBodyimport okhttp3.ResponseBodyimport org.json.JSONObjectimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryclass CialoApiClient : IApiClient {    companion object {        private val jsonSerializer: Gson = Gson()        private fun retrofit(): RetrofitApiClient {            return Retrofit.Builder()                .baseUrl(BuildConfig.CialoApiUrl)                .addCallAdapterFactory(RxJava2CallAdapterFactory.create())                .addConverterFactory(GsonConverterFactory.create())                .build()                .create(RetrofitApiClient::class.java);        }        private fun createJson(data: Any): RequestBody {            return RequestBody.create(MediaType.parse("application/json"), Gson().toJson(data))        }    }    override suspend fun getRegions(): List<RegionDto> {        return listOf(RegionDto("tag-grunwald", "identifier"))    }    override suspend fun login(model: LoginApiModel): Operation {        return try {            val response = retrofit().login(model);            if (response.isSuccessful) {                Operation.createSuccess();            } else {                Operation.createFailed(readError(response.errorBody(), response.message()));            }        } catch (ex: Exception) {            Operation.createFailed(ex.toString());        }    }    override suspend fun sendBeaconEvents(        providerId: String,        events: BeaconEventsApiModel,    ): Operation {        return try {            val response = retrofit().sendBeaconEvents(providerId, events);            if (response.isSuccessful) {                Operation.createSuccess();            } else {                Operation.createFailed(readError(response.errorBody(), response.message()));            }        } catch (ex: Exception) {            Operation.createFailed(ex.toString());        }    }    private fun readError(response: ResponseBody?, default: String): String {        if (response == null) {            return default;        }        return try {            val responseString = response.string();            JSONObject(responseString).toString()        } catch (e: java.lang.Exception) {            default;        }    }}