package com.example.cialo.services.apiimport com.example.cialo.BuildConfigimport com.google.gson.Gsonimport com.jakewharton.retrofit2.adapter.rxjava2.RxJava2CallAdapterFactoryimport okhttp3.MediaTypeimport okhttp3.RequestBodyimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport retrofit2.http.Bodyclass CialoApiClient : IApiClient {    companion object {        private val jsonSerializer: Gson = Gson()        private fun retrofit(): IApiClient {            return Retrofit.Builder()                .baseUrl(BuildConfig.CialoApiUrl)                .addCallAdapterFactory(RxJava2CallAdapterFactory.create())                .addConverterFactory(GsonConverterFactory.create())                .build()                .create(IApiClient::class.java);        }        private fun createJson(data: Any): RequestBody {            return RequestBody.create(MediaType.parse("application/json"), Gson().toJson(data))        }    }    override suspend fun getRegions(): List<RegionDto> {        return listOf(RegionDto("tag-grunwald", "identifier"))    }    override suspend fun login(model: LoginApiModel): Boolean {        return try {            retrofit().login(model)        } catch (ex: Exception) {            false;        }    }}