package com.example.cialo.services.apiimport com.google.gson.Gsonimport okhttp3.MediaTypeimport okhttp3.RequestBodyimport retrofit2.Retrofitimport retrofit2.http.Bodyclass CialoApiClient : IApiClient {    override suspend fun sendEnter(@Body requestBody: RequestBody) {        val retrofit = Retrofit.Builder()            .baseUrl("https://cialo-platform.azurewebsites.net")            .build()        val gson = Gson()        val body = gson.toJson(requestBody)        var requestBody = RequestBody.create(MediaType.parse("application/json"), body)        return retrofit.create(IApiClient::class.java).sendEnter(requestBody)    }    override suspend fun getRegions(): List<RegionDto> {        return listOf(RegionDto("tag-grunwald", "identifier"))    }}